-- c++ objects instances
local Map = {};
local ObjectManager = {};
local DialogueManager = {};
local EventManager = {};
local GlobalEvents = {};

-- the main character handler
local bronann = {};

-- the main map loading code
function Load(m)

	Map = m;
	ObjectManager = Map.object_supervisor;
	DialogueManager = Map.dialogue_supervisor;
	EventManager = Map.event_supervisor;

	Map.unlimited_stamina = true;

	_CreateCharacters();
	_CreateObjects();

	-- Set the camera focus on bronann
	Map:SetCamera(bronann);

	_CreateEvents();
	_CreateZones();

	-- The only entrance close door sound
	AudioManager:PlaySound("snd/door_close.wav");
end

-- the map update function handles checks done on each game tick.
function Update()
	-- Check whether the character is in one of the zones
	_CheckZones();
end

-- Character creation
function _CreateCharacters()
	bronann = CreateSprite(Map, "Bronann", 28, 15);
	bronann:SetDirection(hoa_map.MapMode.SOUTH);
	bronann:SetMovementSpeed(hoa_map.MapMode.NORMAL_SPEED);

	Map:AddGroundObject(bronann);
end

function _CreateObjects()
	object = {}

	-- Bronann's room
	object = CreateObject(Map, "Bed1", 34, 28);
	if (object ~= nil) then Map:AddGroundObject(object) end;

	local chest = CreateTreasure(Map, "sophia_house_chest", "Wood_Chest1", 21, 22);
	if (chest ~= nil) then
		chest:AddObject(1, 1);
		Map:AddGroundObject(chest);
	end

	object = CreateObject(Map, "Chair1_inverted", 31, 19);
	if (object ~= nil) then Map:AddGroundObject(object) end;
	object = CreateObject(Map, "Small Wooden Table", 34, 20);
	if (object ~= nil) then Map:AddGroundObject(object) end;


	object = CreateObject(Map, "Left Window Light", 21, 21);
	object:SetCollisionMask(hoa_map.MapMode.NO_COLLISION);
	object:SetDrawOnSecondPass(true); -- Above any other ground object
	if (object ~= nil) then Map:AddGroundObject(object) end;

	object = CreateObject(Map, "Flower Pot2", 21, 24);
	if (object ~= nil) then Map:AddGroundObject(object) end;

	object = CreateObject(Map, "Right Window Light", 35, 21);
	object:SetDrawOnSecondPass(true); -- Above any other ground object
	object:SetCollisionMask(hoa_map.MapMode.NO_COLLISION);
	if (object ~= nil) then Map:AddGroundObject(object) end;
end


-- Creates all events and sets up the entire event sequence chain
function _CreateEvents()
	local event = {};
	local dialogue = {};
	local text = {};

	-- Triggered events
	event = hoa_map.MapTransitionEvent("exit floor", "dat/maps/layna_village/layna_village_center.lua", "from sophia's house");
	EventManager:RegisterEvent(event);

end

-- Create the different map zones triggering events
function _CreateZones()
	-- N.B.: left, right, top, bottom
	room_exit_zone = hoa_map.CameraZone(26, 30, 12, 13, hoa_map.MapMode.CONTEXT_01);
	Map:AddZone(room_exit_zone);
end

-- Check whether the active camera has entered a zone. To be called within Update()
function _CheckZones()
	if (room_exit_zone:IsCameraEntering() == true) then
		bronann:SetMoving(false);
		EventManager:StartEvent("exit floor");
		AudioManager:PlaySound("snd/door_open2.wav");
	end
end


